import React, { Component } from 'react';

class TopHeader extends Component {
    constructor() { 
        
        super() 
 
        var today = new Date(),
    
        date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
              
        this.state = {currentDate: date}
    
      }
    render() {
        return (
            <div>
                <h1> { this.state.currentDate }</h1>
            </div>
        )
    }
}

export default TopHeader
