import React from 'react';
import './App.css';

function Todo({ todo }) {
  return (
    <div className="todo">
      {todo.text}
    </div>
  );
};


function App() {

  const [todos, setTodos] = React.useState([
    { text: "Take Kids to School" },
    { text: "Attend Daily Scrum" },
    { text: "Have Lunch with Marketing Team" },
    { text: "New Tech Presentation" }
  ]);


  return (
    <div className="App">
      <div className="todo-list">
        {todos.map((todo, index) => (
          <Todo
            key={index}
            index={index}
            todo={todo}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
